    {
      "id": "R1-general-net-lot-area",
      "title": "Gross vs. net lot area – general calculation and applicability",
      "Qualifiers": {
        "scope": "City of Palo Alto, California",
        "applicability": [
          "All lots used to determine development potential",
          "Applicable at time of calculating lot area for zoning/entitlement review"
        ],
        "exclusions": [
          "None at the general-principle level; specific excluded area types are detailed in related rules R2, R3, and R4"
        ],
        "notes": [
          "For most lots in Palo Alto, gross lot area equals net lot area because no excluded features are present"
        ]
      },
      "Variables": [
        {
          "name": "GrossLotArea",
          "description": "Total lot area measured from lot line to lot line before any exclusions",
          "type": "area",
          "unit": null,
          "source": "Recorded parcel map/survey"
        },
        {
          "name": "NetLotArea",
          "description": "Lot area used to determine development potential after subtracting all required exclusions",
          "type": "area",
          "unit": null,
          "source": "Derived"
        },
        {
          "name": "ExcludedAreas",
          "description": "Collection of areas to subtract from gross lot area",
          "type": "array(area)",
          "unit": null,
          "elements": [
            "AreaWithinPublicROW (see R2)",
            "FlagLotPoleArea (see R3)",
            "CreekChannelArea (see R4)"
          ]
        }
      ],
      "Constants": [
        {
          "name": "DevelopmentPotentialBasis",
          "value": "NetLotArea",
          "unit": null,
          "spec_or_standard": "Local planning practice as stated",
          "procedure": "Use NetLotArea, not GrossLotArea, for all development potential calculations"
        },
        {
          "name": "ComputationFormula",
          "value": "NetLotArea = GrossLotArea − Σ(ExcludedAreas)",
          "unit": null,
          "spec_or_standard": "Derived from policy statement",
          "procedure": "Subtract all applicable excluded area components"
        }
      ],
      "Conditions": [
        {
          "type": "mandatory",
          "logic": "Use NetLotArea to determine development potential"
        },
        {
          "type": "conditional",
          "logic": "If no excluded area types (R2, R3, R4) are present, then NetLotArea = GrossLotArea"
        }
      ],
